package com.kaizen.controllstatements;

public class dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = -2;
		do {
			System.out.println(i);
			i++;
		}
		while (i<=10);
		System.out.println("end");
	
	do {
		System.out.println(i);
		//j++;
	}
	while (i==0);
	System.out.println("end");
}

}
