package com.kaizen.controllstatements;

public class oddnumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 11;
		for(int b = 0; b<=a; b+=2) {
			System.out.println(3%2 + b);
		}
	}
}
		
