package com.java.kaizen.operator;

public class MathsOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.sqrt(64));
		System.out.println(Math.pow(2, 03));
		System.out.println(Math.sin(60));
		System.out.println(Math.cos(45));
		System.out.println(Math.tan(90));
		System.out.println(Math.atan(90));
		System.out.println(Math.atan2(2, 05));
		
		
		System.out.println(1+2+"Hello");
		System.out.println("Hello"+1+2);

	}

}
